import React, { useState, useEffect } from 'react';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import { AlertCircle } from 'lucide-react';
import Alert from './ui/alert/Alert';
import AlertDescription from './ui/alert/AlertDescription';

const Main = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [csrfToken, setCsrfToken] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [statuses, setStatuses] = useState([]);
  const [postures, setPostures] = useState([]);
  const [error, setError] = useState(null);

  const postureTypes = [
    { value: 0, name: 'Downstair', color: '#FF4B4B' },
    { value: 1, name: 'Upstair', color: '#FFB72B' },
    { value: 2, name: 'Running', color: '#50C878' },
    { value: 3, name: 'Sitdown', color: '#4169E1' },
    { value: 4, name: 'StandUp', color: '#9370DB' },
    { value: 5, name: 'Walking', color: '#FF69B4' },
    { value: 6, name: 'Fall', color: '#8B0000' }
  ];

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch('/api/check_login/');
        const data = await response.json();
        setIsLoggedIn(data.is_logged_in);

        if (data.is_logged_in) {
          const userResponse = await fetch('/api/user_info/');
          const userData = await userResponse.json();
          setUserInfo(userData);
          fetchUserStatus();
          fetchUserPosture();
          updateUserStatus();
          updateUserPosture();
        }
      } catch (error) {
        setError('Failed to check login status');
      }
    };

    const fetchCsrfToken = async () => {
      try {
        const response = await fetch('/api/csrf_token/');
        const data = await response.json();
        setCsrfToken(data.csrfToken);
      } catch (error) {
        setError('Failed to fetch CSRF token');
      }
    };

    checkLoginStatus();
    fetchCsrfToken();

    const interval = setInterval(() => {
      if (isLoggedIn) {
        updateUserStatus();
        updateUserPosture();
      }
    }, 10 * 60 * 1000);

    return () => clearInterval(interval);
  }, [isLoggedIn]);

  const fetchUserStatus = async () => {
    try {
      const response = await fetch('/api/get_status/');
      const data = await response.json();
      setStatuses(data.statuses);
    } catch (error) {
      setError('Failed to fetch user status');
    }
  };

  const fetchUserPosture = async () => {
    try {
      const response = await fetch('/api/get_user_posture/');
      const data = await response.json();

      // Transform data for Recharts
      const transformedData = data.postures.map(item => ({
        time: new Date(item.updated_at).toLocaleString(),
        posture: item.posture,
        ...postureTypes.reduce((acc, type) => ({
          ...acc,
          [type.name]: item.posture === type.value ? 1 : 0
        }), {})
      }));

      setPostures(transformedData);
    } catch (error) {
      setError('Failed to fetch user posture');
    }
  };

  const updateUserStatus = async () => {
    try {
      await fetch('/api/update_status/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': csrfToken || ''
        }
      });
      fetchUserStatus();
    } catch (error) {
      setError('Failed to update user status');
    }
  };

  const updateUserPosture = async () => {
    try {
      await fetch('/api/update_user_posture/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': csrfToken || ''
        }
      });
      fetchUserPosture();
    } catch (error) {
      setError('Failed to update user posture');
    }
  };

  const handleLogout = async () => {
    try {
      await fetch('/api/logout/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': csrfToken || ''
        }
      });
      setIsLoggedIn(false);
      setUserInfo(null);
    } catch (error) {
      setError('Failed to logout');
    }
  };

  return (
    <div className="p-4">
      <div className="flex justify-end mb-4">
        {isLoggedIn ? (
          <button 
            onClick={handleLogout}
            className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
          >
            Logout
          </button>
        ) : (
          <div>
            <button className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 mr-2">
              Login
            </button>
            <button className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600">
              Register
            </button>
          </div>
        )}
      </div>

      <div className="text-center mb-8">
        {isLoggedIn && userInfo ? (
          <h1 className="text-2xl font-bold">
            {userInfo.name}님 ({userInfo.age}세), 현재 웹에 접속하셨습니다!
          </h1>
        ) : (
          <h1 className="text-2xl font-bold">Welcome to the Main Page</h1>
        )}
      </div>

      {error && (
        <Alert variant="destructive" className="mb-4">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <div className="mb-8">
        <LineChart width={800} height={400} data={statuses}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="updated_at" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="status" stroke="#8884d8" />
        </LineChart>
      </div>

      <div>
        <BarChart
          width={800}
          height={400}
          data={postures}
          layout="vertical"
          margin={{ top: 20, right: 30, left: 100, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis type="number" />
          <YAxis type="category" dataKey="time" />
          <Tooltip />
          <Legend />
          {postureTypes.map(type => (
            <Bar
              key={type.name}
              dataKey={type.name}
              stackId="posture"
              fill={type.color}
            />
          ))}
        </BarChart>
      </div>
    </div>
  );
};

export default Main;
